// PTPRealIngestScenario returns the real-world PTP calibration scenario
// built from ingested CI results across multiple OCP versions.
func PTPRealIngestScenario() *calibrate.Scenario {
	rcas := ptpRealIngestRCAs()
	verified := make(map[string]bool, len(rcas))
	for _, r := range rcas {
		verified[r.ID] = r.Verified
	}

	var cases, candidates []calibrate.GroundTruthCase
	for _, c := range ptpRealIngestAllCases() {
		if verified[c.RCAID] {
			cases = append(cases, c)
		} else {
			candidates = append(candidates, c)
		}
	}

	return &calibrate.Scenario{
		Name:        "ptp-real-ingest",
		Description: "Real PTP failures: 18 verified + 12 candidate cases from CI data (Jan 2025 - Feb 2026)",
		RCAs:        rcas,
		Symptoms:    ptpRealIngestSymptoms(),
		Cases:       cases,
		Candidates:  candidates,
		Workspace:   ptpRealIngestWorkspace(),
	}
}

